import React from 'react';
import { MovieCard } from '../movie-card/movie-card';
import { MovieView } from '../movie-view/movie-view';

export class MainView extends React.Component {
    constructor(){
        super();
        this.state = {
          movies: [
            { _id: 1, 
                Title: 'Inception', 
                Description: 'Cobb and Arthur are "extractors"; they perform corporate espionage using experimental dream-sharing technology to infiltrate their targets\' subconscious and extract information. Their latest target, Saito, is impressed with Cobb\'s ability to layer multiple dreams within each other and offers to hire Cobb for the supposedly impossible job of implanting an idea into a person\'s subconscious; performing "inception" on Robert, the son of Saito\'s competitor Maurice Fischer, with the idea to dissolve his father\'s company. Saito promises to clear Cobb\'s criminal status, allowing him to return home to his children.',
                Genre: 'science fiction action',
                Director: 'Christopher Nolan',
                 ImagePath: 'data:image/jpeg;base64,'
            },
            { _id: 2,
                Title: 'The Sawshank Redemption', 
                Description: 'In 1947 Portland, Maine, banker Andy Dufresne is convicted of murdering his wife and her lover and is sentenced to two consecutive life sentences at the Shawshank State Prison. He is befriended by Ellis "Red" Redding, an inmate and prison contraband smuggler serving a life sentence. Red procures a rock hammer and a large poster of Rita Hayworth for Andy. Assigned to work in the prison laundry, Andy is frequently sexually assaulted by "the Sisters" and their leader, Bogs.', 
                Genre: 'drama',
                Director: 'Frank Darabont ',
                ImagePath: 'data:image/jpeg;base64,'
              },
              
            { _id: 3, 
              Title: 'Gladiator', 
              Description: 'In 180 AD, Hispano-Roman General Maximus Decimus Meridius intends to return home after he leads the Roman army to victory against the Germanic tribes near Vindobona on the Limes Germanicus. Emperor Marcus Aurelius tells Maximus that his own son, Commodus, is unfit to rule and that he wishes Maximus to succeed him, as regent, to help save Rome from corruption and restore the republic. Upon hearing this, Commodus murders his father.',
              Genre: 'historical drama',
              Director: 'Ridley Scott',
               ImagePath: 'data:image/jpeg;base64,'
              }
          ],
          selectedMovie: null
        }
    }
    setSelectedMovie(newSelectedMovie) {
        this.setState({
          selectedMovie: newSelectedMovie
        });
      }
    
    render() {
        const {movies, selectedMovie } = this.state;
        
        if (movies.length === 0) return <div className="main-view">The list is empty!</div>;

          return (
            <div className="main-view"> 
            {selectedMovie
          ? <MovieView movie={selectedMovie} onBackClick={newSelectedMovie => { this.setSelectedMovie(newSelectedMovie); }}/>
          : movies.map(movie => (
            <MovieCard key={movie._id} movie={movie} onMovieClick={(movie) => { this.setSelectedMovie(movie) }}/>
          ))
        }
            </div>
          );    
        }
    }
